import React, { Component } from 'react'
import { showAllProject } from '../../actions/projectAction.js'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
// import Project from './Project.js'
class AsClient extends Component {

    componentDidMount(){
        this.props.showAllProject();
    }
     componentWillReceiveProps(nextProps){
            if(nextProps.errors){
                this.setState({
                    project: nextProps.project
                });
            }
     }

    render() {
        const { project } = this.props;


     
        return (
            <div>
            {project.name}
            </div>
        )
    }
}
AsClient.propTypes = {
  showAllProject: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  project: PropTypes.object.isRequired
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  project: state.project
});
export default connect(mapStateToProps, {  showAllProject })(AsClient);
