import React, { Component } from 'react'
import "../../Css/EditProfile.css";
import { setProfileData } from '../../actions/profileAction.js';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { withRouter } from "react-router-dom";




 class EditProfile extends Component {
     constructor(){
         super();
             this.state = {
                 username: '',
                 phone: '',
                 profession:'',
                 hourlyRate:'',
                 availibilty:'',
                 age:'',
                 address:'',
                 experience:'',
                 github:'',
                 behance:'',
                 facebook:'',
                 linkedin:'',
                 instagram:'',
                 portfolio:'',
                 photo:'',
                 cv:'',
                 certficates:'',
                 descrption:'',
                 languages:'',
                 skills:''


             };
         this.onChange = this.onChange.bind(this);
         this.onSubmit = this.onSubmit.bind(this);

     }
     onChange(e){
         this.setState({[e.target.name]: e.target.value});
     }
      onSubmit(e){
      e.preventDefault();
         
         const editProfile = {
           username: this.state.username,
           phone: this.state.phone,
           profession: this.state.profession,
           hourlyRate: this.state.hourlyRate,
           availibilty: this.state.availibilty,
           age: this.state.age,
           address: this.state.address,
           experience: this.state.experience,
           github: this.state.github,
           behance: this.state.behance,
            facebook: this.state.facebook,
           linkedin: this.state.linkedin,
           instagram: this.state.instagram,
           portfolio: this.state.portfolio,
           photo: this.state.photo,
           cv: this.state.facebook,
           certficates: this.state.certficates,
           descrption: this.state.descrption,
           languages: this.state.languages,
           skills: this.state.skills,
         };
         this.props.setProfileData(editProfile, this.props.history);
     }
    render() {
        return (
            <div className="editProfile">
                <div className="navName">
                    <h4>Edit Profile</h4>
                    <p>Add your profile Information.</p>
                </div>
                <hr/>
                <div className="editProfile_body">
                     <form onSubmit={this.onSubmit}>
                        <div className="col1">
                                <h5>Name</h5>
                                <input type='text'   name= 'username' value={this.state.name}   
                                onChange={this.onChange} 
                                />

                                <h5>Phone</h5>
                                <input type='text'name= 'phone' value={this.state.name}
                                onChange={this.onChange}  
                                />

                                <h5>Profession</h5>
                                <input type='text'  name= 'profession'value={this.state.name} 
                                onChange={this.onChange} 
                                />

                                <h5>Hourly Rate</h5>
                                <input type='text'name= 'hourlyRate' value={this.state.name}
                                 onChange={this.onChange}  
                                />
                                       
                                 <h5>Photo</h5>
                                <input type='file'   name= 'photo' value={this.state.name} 
                                onChange={this.onChange} 
                                />

                                <h5>Availibilty Hrs/Week</h5>
                                <input type='text'name= 'availibilty' value={this.state.name}
                                onChange={this.onChange}  
                                />
                                        
                                <h5>Age</h5>
                                <input type='text' name= 'age'value={this.state.name}
                                onChange={this.onChange}  
                                />
                                                
                                <h5>Address</h5>
                                <input type='text'  name= 'address'value={this.state.name}
                                onChange={this.onChange}  
                                />
                                                
                       </div>

                        <div className="col2">
                                <h5>Experience</h5>
                                <input type='text'  name= 'experience'value={this.state.name} 
                                onChange={this.onChange} 
                                />

                                <h5>Github</h5>
                                <input type='text'
                                name= 'github'
                                value={this.state.name}
                                onChange={this.onChange}  
                                />

                                <h5>Behance</h5>
                                <input type='text'   name= 'behance' value={this.state.name} 
                                onChange={this.onChange} 
                                />

                                <h5>Facebook</h5>
                                <input type='text' name= 'facebook'  value={this.state.name}
                                onChange={this.onChange}  
                                />

                                <h5>Instagram</h5>
                                <input type='text'   name= 'instagram' value={this.state.name} 
                                onChange={this.onChange} 
                                />

                                <h5>LinkedIn</h5>
                                <input type='text' name= 'linkedin'value={this.state.name}
                                onChange={this.onChange}  
                                />
                                        
                                <h5>Portfolio</h5>
                                <input type='text' name= 'portfolio' value={this.state.name}
                                onChange={this.onChange}  
                                />

                        </div>

                        <div className="col3">

                                <h5>Description</h5>
                                <textarea type='text'  name= 'experience' value={this.state.name} 
                                onChange={this.onChange} 
                                />

                                <h5>C.V</h5>
                                <input type='file'name= 'cv' value={this.state.name}
                                onChange={this.onChange}  
                                />
                                        
                                <h5>Certificates</h5>
                                <input type='file'  name= 'certificates'  value={this.state.name}
                                onChange={this.onChange}  
                                />

                                <h5>Languages</h5>
                                <input type='text'  name= 'languages' value={this.state.name} 
                                onChange={this.onChange} 
                                />

                                <h5>Skills</h5>
                                <input type='text'name= 'skills' value={this.state.name}
                                onChange={this.onChange}  
                                 />
                
                       </div>
                        <button type='submit' className='formSubmit'>Submit</button>
                </form>
                </div>
            </div>
        )
    }
}
EditProfile.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});
export default connect(mapStateToProps, {  setProfileData })(withRouter(EditProfile));
