import {
  GET_MESSAGES,
  MESSAGE_LOADING,
  GET_MESSAGE_ROOMS,
  CLEAR_MESSAGE

} from '../actions/types';

const initialState = {
  messages: null,
  messageRooms: null,
  loading: false
};

function messageReducer(state = initialState, action) {

  switch (action.type) {
     case MESSAGE_LOADING:
      return {
        ...state,
        loading: true
        
      };
      case GET_MESSAGES:
      return {
        ...state,
        messages: action.payload,
        loading:false
      };
      case GET_MESSAGE_ROOMS:
      return {
        ...state,
        messageRooms: action.payload,
        loading:false
      };
      case CLEAR_MESSAGE:
      return {
        ...state,
         loading: true,
        messages: [...state.messages.filter(message => message.id !== action.id)]

      };
    default:
      return state;
  }
}

export default messageReducer;
