import React, { Component }  from 'react';
import "../../Css/User.css";
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { logout } from '../../actions/authAction.js';
import { withRouter } from "react-router-dom";

class User extends Component {

//    logoutUser(e){
//          e.preventDefault();
//          this.props.logout();
//           this.props.history.push('/');
//      }
      render() {
          const { isAuthenticated, user} = this.props.auth;
    return (
        <div className="user">
         
                        {/* <button onClick={this.logoutUser.bind(this)}
                         className='loginBtn'>LOGOUT</button> */}
                        <div className="navbar">
                          <div className="user">
                              <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                              <p>user.email</p>
                          </div>
                           <div className="navName">
                              <ul>
                                  <li>Dashboard</li>
                                  <li>Profile</li>
                                  <li>Inbox</li>
                                  <li>All Projects</li>
                                  <li>Post Project</li>
                              </ul>
                           </div>
                        </div>
                        <div className="navbody">This is navbody</div>

        </div>
    )
      }
}



User.propTypes = {
  user: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps,{  logout })(withRouter(User));
