import React , { useState } from 'react'
import "../../Css/Register.css"
import { Link, Redirect } from "react-router-dom";
import { useDispatch } from 'react-redux';


import { connect } from 'react-redux';
import { register } from '../../actions/authAction';
import PropTypes from 'prop-types';







const Register = () => {
 const dispatch = useDispatch();
     const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
    const registerBtn = e => {
         e.preventDefault();
         dispatch({ type: 'REGISTER_USER' })
         console.log(formData)
         
  // if (isAuthenticated) {
  //   return <Redirect to="/user" />;
  // }
    }
    return (
        <div className="register">
            <Link to="/" style={{textDecoration:0}}>
                 <h1 className="ejobs">eJobs</h1>
            </Link>
            <div className="registerBox">
                <h3>Create Account</h3> 
                <form className="registerForm">
                    <h5>Email</h5>
                    <input type='text'  
                    value={formData.email} onChange={(e) => setFormData({ ...formData, email: e.target.value })} 
                     />

                    <h5>Password</h5>
                    <input type='password' 
                      value={formData.password} onChange={(e) => setFormData({ ...formData, password: e.target.value })} 
                             />
                    <div className="other">
                        <div>Have Account.<Link to="/login" style={{textDecoration:0}}><span className="accountRouting">Login</span></Link></div>
                        <button type='submit' onClick={registerBtn} className='registerBtn'>Register</button>
                    </div>
                </form>
                  <p>
                    By signing-in you agree to the ejobs Conditions. Please
                    see our Privacy Notice, our Cookies Notice and our Interest-Based Ads Notice.
                </p>
       
            </div>
               
        </div>
    )
}

Register.propTypes = {
  errors: PropTypes.bool
};

const mapStateToProps = (state) => ({
  isAuthenticated: state.auth.isAuthenticated
});

export default connect(mapStateToProps, { register })(Register);
