import React, { Component } from 'react'
import "../../Css/Chat.css";
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { sendMessage } from '../../actions/messageAction.js';

 class Chat extends Component {
    constructor(){
         super();
             this.state = {
                 message: '',
             };
         this.onChange = this.onChange.bind(this);
         this.onSubmit = this.onSubmit.bind(this);

     }
      onChange(e){
         this.setState({[e.target.name]: e.target.value});
     }
      onSubmit(e){
         e.preventDefault();   
         const newMessage = {
           message: this.state.email,
         };
         this.props.sendMessage(newMessage);
     }
    render() {
        const { messages } = this.props;
        return (
                  <div className="chat"> 
                           <div className="topInfo">
                               <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                               {/* <h5>{messages.otherPerson}</h5> */}
                           </div>
                            <div className="messageBody">
                                    <div className="messageArea">
                                        {/* <div className="clientMessage">
                                            <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                                            <p>Message of the client sent by them</p>
                                        </div> */}
                                        

                                        {/* {messages.messages.map((message , i) =>
                                        <div className="userMessage">
                                            <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                                            <p>{message.message}</p>
                                        </div>
                                        )} */}
                                    </div>
                                    <div className="messageForm">
                                        <form autoComplete="off"  onSubmit={this.onSubmit}>
                                            <input  name= 'message'
                                                value={this.state.name} 
                                                onChange={this.onChange} type="text"/>
                                        </form>
                                    </div>
                            </div>
              
            </div>
        )
    }
}

Chat.propTypes = {
      sendMessage: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
    
});

export default connect(mapStateToProps, { sendMessage  })(Chat);
