import {
  REGISTER_USER,
} from '../actions/types';

const initialState = {
  profile: {},
  profiles: {},
  loading: false
};



function authReducer(state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
     case REGISTER_USER:
      return {
        ...state,
        payload
        
      };
    default:
      return state;
  }
}

export default authReducer;
