import React, { Component } from 'react'
import "../../Css/Dashboard.css";
import { showAllProject } from '../../actions/projectAction.js'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import NavName from './NavName'
import Project from './Project';


 class Dashboard extends Component {
   

   componentDidMount(){
        this.props.showAllProject();
    }
     componentWillReceiveProps(nextProps){
            if(nextProps.project){
                this.setState({
                    project: nextProps.project
                });
            }
     }
 
    render() {
        //  const { project } = this.props.project;
         
        const display = (
            <div>
                {/* <Project project={project}/> */}
            </div>
        ); 
        return (
            <div className="dashboard">
                <NavName pageName="DashBoard" pageDescription="See all the project listed on eJob. And Apply."  />
                <div className="dashBoard_body">
                    {display}
                </div>
            </div>
        )
    }
}
Dashboard.propTypes = {
  showAllProject: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  project: PropTypes.object.isRequired
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  project: state.project
});
export default connect(mapStateToProps, {  showAllProject })(Dashboard);
