import React, { Component }  from 'react';
import "../../Css/User.css";
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { logout } from '../../actions/authAction.js';
import { withRouter } from "react-router-dom";
import { RiDashboardLine, RiMessage2Line, RiUserLine ,RiLogoutBoxRLine} from "react-icons/ri";
import {  BsFilePost } from "react-icons/bs";
import {  AiOutlineProject } from "react-icons/ai";




class User extends Component {

   logoutUser(e){
         e.preventDefault();
         this.props.logout();
          this.props.history.push('/');
     }
      render() {
        //   const { isAuthenticated, user} = this.props.auth;
    return (
        <div className="user">
         
                        
                        <div className="navbar">
                          <div className="user">
                              <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                              <p>user.email</p>
                          </div>
                           <div className="navname">
                        <ul>
                            <li><RiDashboardLine style={{color: 'gray',fontSize:20, marginRight:25}}/>Dashboard</li>
                            <li><RiUserLine style={{color: 'gray',fontSize:20, marginRight:25}}/>Profile</li>
                            <li><RiMessage2Line style={{color: 'gray',fontSize:20, marginRight:25}}/>Inbox</li>
                            <li><AiOutlineProject style={{color: 'gray',fontSize:20, marginRight:25}}/>All Projects</li>
                            <li><BsFilePost style={{color: 'gray',fontSize:20, marginRight:25}}/>Post Project</li>
                            <li onClick={this.logoutUser.bind(this)}><RiLogoutBoxRLine style={{color: 'gray',fontSize:20, marginRight:25}}/>Logout </li>
                        </ul>
                           </div>

                        </div>
                        <div className="navbody">This is navbody</div>

        </div>
    )
      }
}



User.propTypes = {
  user: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps,{  logout })(withRouter(User));
