import {
   GET_MESSAGE,
  MESSAGE_LOADING,
} from '../actions/types';

const initialState = {
  message: null,
  loading: false
};

function messageReducer(state = initialState, action) {

  switch (action.type) {
     case MESSAGE_LOADING:
      return {
        ...state,
        loading: true
        
      };
      case GET_MESSAGE:
      return {
        ...state,
        message: action.payload,
        loading:false
      };
     
    default:
      return state;
  }
}

export default messageReducer;
