import React, { Component } from 'react'
import "../../Css/Profile.css";
import {  RiGithubFill,} from "react-icons/ri";
import {  FaFacebookF, FaInstagram , FaDribbble , FaUserEdit} from "react-icons/fa";
import { AiOutlineLinkedin  } from "react-icons/ai";
import { Link} from "react-router-dom";
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { getCurrentProfile } from '../../actions/profileAction.js';
import NavName from './NavName'
import Experience from './Experience';
import Education from './Education';
import Spinner from '../../Spinner.js'
import ProfileData from './ProfileData'

import isEmpty from '../../validation/isEmpty.js';


 class Profile extends Component {

    componentDidMount(){
        this.props.getCurrentProfile();
       
    }
    componentWillReceiveProps(nextProps){
          
                this.setState({
                     profile: nextProps.profile,
                });
                
     }
    render() {
        const { profile, loading} = this.props.profile; 
          let AllProfile;
        let  userExperience;
        let userEducation;
           if (profile === null || loading) {
      AllProfile = <Spinner />;
    } else {
      AllProfile =  <div >
          <ProfileData profile={profile}/>
      </div>
    }
     if(profile.education === null || loading){
         userEducation = <Spinner/>   
     }
     else{
           userEducation = <div>
                <Education education = {profile.education} />
           </div> 
     }

     if (profile.workexperience === null || loading) {
      userExperience = <Spinner />;
    } else {
      userExperience =  <div>
                               <Experience workexperience={profile.workexperience} />
                       </div>;
    }
    
          
        return (
            <div className="profile">
                   <NavName pageName="Profile" pageDescription="Add or Update profile information."  />
                   <Link to="/editProfile" style={{textDecoration:0}}><button className="editBtn"><FaUserEdit style={{color: 'black',fontSize:20, marginRight:10}}/> Edit Profile </button>
                                    </Link>
                
                    {/* {AllProfile}
                <div className="profileCredentials">
                     <div className="education">
                        {userEducation}
                    </div>
                    <div className="experience">   
                        {userExperience}
                    </div>
                    
                </div>   */}
               
                
                {AllProfile}
                {userExperience}
                {userEducation}
            </div>
        )
    }
}


Profile.propTypes = {
  getCurrentProfile: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  profile: state.profile,
});
export default connect(mapStateToProps, {  getCurrentProfile })(Profile);
