import React, { Component } from 'react'
import "../../Css/Chat.css";
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { sendMessage } from '../../actions/messageAction.js';
import { FiSend } from "react-icons/fi";

 class Chat extends Component {
      constructor(){
         super();
             this.state = {
                message: '',
             };
         this.onChange = this.onChange.bind(this);

     }
    onChange(e){
         this.setState({[e.target.name]: e.target.value});
     }
     
     
      onSendMessageClick(roomName){ 
         const newMessage = {
           text: this.state.message,
         };
        console.log(newMessage);
        this.props.sendMessage(newMessage);
     }
    render() {
          const { roomname  } = this.props.message;
        const { allMessages, user, allUsers } = this.props;

        return (
                  <div className="chat"> 
                           <div className="topInfo">
                               <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                               <h5>{allUsers.map((alluser,i) => 
                               alluser._id === roomname ? (<h5>{allUsers.email}</h5>): (null))}</h5>
                           </div>
                            <div className="messageBody">
                                    <div className="messageArea">
                                        {allMessages.map((message , i) =>
                                  message.fromPerson ===  user.user.id ? (<div className="userMessage">
                                            <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                                            <p>{message.text}</p>
                                        </div>) : (<div className="clientMessage">
                                            <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                                            <p>{message.text}</p>
                                        </div>)
                                        
                                        )}
                                    </div>
                                    <div className="messageForm">
                                        <form autoComplete="off" >
                                            <input  name ='message'
                                                value={this.state.name} 
                                                onChange={this.onChange} type="text"/>
                                            <button onClick={this.onSendMessageClick.bind(this)} className="sendMsg" type='submit'><FiSend style={{color: 'gray',fontSize:20, marginRight:25}}/></button>
                                        </form>
                                    </div>
                            </div>
              
            </div>
        )
    }
}

Chat.propTypes = {
      sendMessage: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
      message: state.message
    
});

export default connect(mapStateToProps, { sendMessage  })(Chat);
