import './App.css';
import Landing from './Components/Landing/Landing';
import Register from './Components/Account/Register';
import Login from './Components/Account/Login';
import User from './Components/LoggedUser/User';
import React  from 'react'
import { BrowserRouter as Router, Route, Switch} from "react-router-dom";
import jwt_decode from 'jwt-decode';
import setAuthToken from './utils/setAuthToken.js'
import  setCurrentUser from './actions/authAction.js'
import store from './store.js'
import { clearCurrentProfile } from './actions/profileAction.js';
import PrivateRoute from './PrivateRoute.js'
import Profile from './Components/LoggedUser/Profile';
import Dashboard from './Components/LoggedUser/Dashboard';


const App = () => {

    if (localStorage.jwtToken) {
      setAuthToken(localStorage.jwtToken);
      const decoded = jwt_decode(localStorage.jwtToken);
     store.dispatch(setCurrentUser(decoded));
      store.dispatch(clearCurrentProfile());
    }


  return (
          <Router>
            <div className="app">
              <Switch>
                  <Route  path="/login" component={Login} />
                  <Route  path="/register" component={Register} />
                  {/* <PrivateRoute  path="/user" component={User} /> */}

                   <Route
                        path="/user"
                        render={({ match: { url } }) => (
                          <>
                            <Route path={`${url}/`} component={User} exact />
                            <Route path={`${url}/dashboard`} component={Dashboard} />
                            <Route path={`${url}/profile`} component={Profile} />
                          </>
                        )}
                      />
                  <Route exact path="/" component={Landing} />
              </Switch>
            </div>
          </Router>
  );
}

export default App;
