import axios from 'axios';
import {
  GET_ERRORS,
  // REGISTER_USER,
} from './types';



// Register User
export const register = formData =>  dispatch => {
   axios
   .post('http://localhost:5000/register', formData)
   .then()
   .catch(err => 
    dispatch({
      type: GET_ERRORS,
      payload:err.response.data
    }))
};
