import React, { Component }  from 'react';
import "../../Css/User.css";
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { logout } from '../../actions/authAction.js';
import { clearCurrentProfile } from '../../actions/profileAction.js';
import AddCredentials from './AddCredentials';
import { withRouter } from "react-router-dom";
import NavBar from './NavBar'
import DashBoard from './Dashboard';
import Profile from './Profile';
import Inbox from './Inbox';
import AllProjects from './AllProjects';
import PostProject from './PostProject';
import EditProfile from './EditProfile';






class User extends Component {

   logoutUser(e){
         e.preventDefault();
          this.props.clearCurrentProfile();
          this.props.logout();
          this.props.history.push('/');
     }
      render() {
          
        //   const { isAuthenticated, user} = this.props.auth;
    return (
        <Router>
        <div className="user">     
            <div className="navbody">
               <NavBar />
                <Switch>
                        <Route  path="/editProfile" component={EditProfile}/>
                            
                        <Route  path="/profile" component={Profile}/>
                             
                        <Route path="/addCredentials" component={AddCredentials}/>
                             
                        <Route  path="/inbox" component={Inbox}/>
                            
                        <Route path="/allprojects" component={AllProjects}/>

                        <Route path="/postproject" component={PostProject}/>
                           
                        <Route   path="/" component={DashBoard}/>  
                       
                            
                </Switch>
            </div>
            

        </div>
    </Router>)
      }
}



User.propTypes = {
  clearCurrentProfile: PropTypes.func.isRequired,
  logout: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps,{  logout, clearCurrentProfile })(withRouter(User));
