import React , { useState } from 'react'
import "../../Css/Login.css"
import { Link } from "react-router-dom";


function Login() {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');


    const loginBtn = e => {
    }
    return (
        <div className="login">
            <Link to="/" style={{textDecoration:0}}>
                 <h1 className="ejobs">eJobs</h1>
            </Link>
            <div className="loginBox">
                <h3>Welcome back</h3> 
                <form className="loginForm">
                    <h5>Email</h5>
                    <input type='text' value={email} onChange={e => setEmail(e.target.value)} />

                    <h5>Password</h5>
                    <input type='password' value={password} onChange={e => setPassword(e.target.value)} />
                    <div className="other">
                           <div>No Account.<Link to="/register" style={{textDecoration:0}}><span className="accountRouting">Register</span></Link></div>
                            <button type='submit' onClick={loginBtn} className='loginBtn'>Login</button>
                        </div>
                </form>
    
       
            </div>
               
        </div>
    )
}

export default Login
