import React, { Component }  from 'react';
import "../../Css/User.css";
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { logout } from '../../actions/authAction.js';
import { clearCurrentProfile } from '../../actions/profileAuth.js';

import { withRouter } from "react-router-dom";
import { RiDashboardLine, RiMessage2Line, RiUserLine ,RiLogoutBoxRLine} from "react-icons/ri";
import {  BsFilePost } from "react-icons/bs";
import {  AiOutlineProject } from "react-icons/ai";
import {  BrowserRouter as Router ,Switch, Route, Link} from "react-router-dom";
import DashBoard from './Dashboard';
import Profile from './Profile';
import Inbox from './Inbox';
import AllProjects from './AllProjects';
import PostProject from './PostProject';
import EditProfile from './EditProfile';






class User extends Component {

   logoutUser(e){
         e.preventDefault();
         this.props.logout();
          this.props.history.push('/');
          this.props.clearCurrentProfile();
     }
      render() {
        //   const { isAuthenticated, user} = this.props.auth;
    return (
        <Router>
        <div className="user">
         
                        
                        <div className="navbar">
                          <div className="user">
                              <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                              <p>user.email</p>
                          </div>
                           <div className="navname">
                        <ul>
                            <Link to="/" style={{textDecoration:0}}><li><RiDashboardLine style={{color: 'gray',fontSize:20, marginRight:25}}/>Dashboard</li></Link>

                            <Link to="/profile" style={{textDecoration:0}}><li><RiUserLine style={{color: 'gray',fontSize:20, marginRight:25}}/>Profile</li></Link> 

                             <Link to="/inbox" style={{textDecoration:0}}><li><RiMessage2Line style={{color: 'gray',fontSize:20, marginRight:25}}/>Inbox</li></Link>

                            <Link to="/allprojects" style={{textDecoration:0}}><li><AiOutlineProject style={{color: 'gray',fontSize:20, marginRight:25}}/>All Projects</li></Link> 

                             <Link to="/postproject" style={{textDecoration:0}}><li><BsFilePost style={{color: 'gray',fontSize:20, marginRight:25}}/>Post Project</li></Link>

                            <li onClick={this.logoutUser.bind(this)}><RiLogoutBoxRLine style={{color: 'gray',fontSize:20, marginRight:25}}/>Logout </li>
                        </ul>
                           </div>

                        </div>
                        <div className="navbody">
                            <Switch>
                               <Route  path="/editProfile">
                                    <EditProfile />
                                </Route>
                                <Route  path="/profile">
                                    <Profile />
                                </Route>
                                    <Route  path="/inbox">
                                    <Inbox />
                                </Route>
                                    <Route path="/allprojects">
                                    <AllProjects />
                                </Route>
                                <Route path="/postproject">
                                    <PostProject />
                                </Route>
                                 <Route  path="/">
                                    <DashBoard />
                                </Route>
                            </Switch>
                        </div>

        </div>
    </Router>)
      }
}



User.propTypes = {
  user: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps,{  logout, clearCurrentProfile })(withRouter(User));
