import axios from 'axios';
import {
  REGISTER_USER,
} from './types';




export const register = formData => async dispatch => {
  try {
    const res = await axios.post('http://localhost:5000/register', formData);
    console.log(res.data);
    dispatch({
      type: REGISTER_USER,
      payload: res.data
    });
   
  
  } catch (err) {
    console.log(err);
  }
};
