import React, { Component } from 'react'
import "../../Css/Profile.css";

import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { getIndividualProfile } from '../../actions/profileAction.js';
import Experience from './Experience';
import Education from './Education';
import Spinner from '../../Spinner.js'


 class Profile extends Component {

    render() {
        const { individualProfile, loading} = this.props.profile; 

         let  userExperience ;
         
        let userEducation ;
            let Allprojects;

    if (individualProfile === null || loading) {
      Allprojects = <Spinner />;
    } else {
      Allprojects = <h1>Yes, There is Profile. His name is {individualProfile.username}. And his phone number is {individualProfile.phone}</h1>;
    }

    if(individualProfile.education === null || loading){
         userEducation = <Spinner/>   
     }
     else{
           userEducation = <div>
                <Education education = {individualProfile.education} />
           </div> 
     }
     if (individualProfile.workexperience === null || loading) {
      userExperience = <Spinner />;
    } else {
      userExperience =  <div>
                               <Experience workexperience={individualProfile.workexperience} />
                       </div>;
    }
          
        return (
             <div>
                {Allprojects}
                {userExperience}
                {userEducation}
            </div>
        )
    }
}


Profile.propTypes = {
  getCurrentProfile: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  profile: state.profile,
});
export default connect(mapStateToProps, {  getIndividualProfile })(Profile);
