import {
  REGISTER_USER
} from '../actions/types';


const initialState = {
  // token: localStorage.getItem('token'),
  // isAuthenticated: null,
  // loading: true,
  // user: null
};



function authReducer(state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
     case REGISTER_USER:
      return {
        ...state,
        ...payload,
      };
    
    default:
      return state;
  }
}


export default authReducer;
