import React, { Component } from 'react'
import "../../Css/Chat.css";
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { sendMessage } from '../../actions/messageAction.js';
import { FiSend } from "react-icons/fi";
import { withRouter } from "react-router-dom";


 class Chat extends Component {
      constructor(){
         super();
            this.state = {
                 text: '',
                 otherPerson: ''
             };
         this.onChange = this.onChange.bind(this);
     }

    onChange(e){
         this.setState({[e.target.name]: e.target.value});
     }
      onSendMessageClick(roomname) {  
                    const newMessage = {
                        text: this.state.text,
                        otherPerson: roomname,
         };
         this.props.sendMessage(newMessage);
        this.setState({text: ''});

             }
    render() {
          const { roomname  } = this.props.message;
        const { messages, user , allUsers } = this.props;
        console.log(roomname);

        

        return (
                  <div className="chat"> 
                  
                           <div className="topInfo">
                               <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                            {allUsers.map((alluser, i) => alluser._id === roomname ? (<h5>{alluser.email}</h5>):(null))}
                           </div>


                            <div className="messageArea">
                                {}
                                 <div className="allMessage">
                                                        {messages.map((message , i) =>
                                    message.fromPerson ===  user.user.id || message.fromPerson ===  roomname ? (
                                  user.user.id  ===  message.fromPerson ? (<div className="userMessage">
                                                <p>{message.text}</p>
                                                    <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/> 
                                                    </div>): (<div className="clientMessage">
                                                    <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                                                <p>{message.text}</p>
                                                
                                            </div>)
                                    ) : ( null )
                                            
                                            )}
                                </div>
                                
                            </div>


                            <div className="messageForm">
                                <form autoComplete="off" >
                                    <input type='text'  
                                        name= 'text'
                                        value={this.state.name} 
                                        onChange={this.onChange} 
                                        />
                                    <button type="reset" onClick={this.onSendMessageClick.bind(this, roomname)} className="sendMsg" ><FiSend style={{color: 'gray',fontSize:20, marginRight:25}}/></button>
                                </form>
                            </div>
              
            </div>
        )
    }
}

Chat.propTypes = {
      sendMessage: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
      message: state.message
    
});

export default connect(mapStateToProps, { sendMessage  })(withRouter(Chat));
