import {
   GET_PROJECT,
  PROJECT_LOADING,
  GET_CLIENT_PROJECT,
  DELETE_PROJECT,
  APPLY_PROJECT
} from '../actions/types';

const initialState = {
  project: null,
  clientProject: null,
  loading: false
};



function profileReducer(state = initialState, action) {

  switch (action.type) {
     case PROJECT_LOADING:
      return {
        ...state,
        loading: true
        
      };
      case GET_PROJECT:
      return {
        ...state,
        project: action.payload,
        loading:false
      };
      
      case GET_CLIENT_PROJECT:
      return {
        ...state,
        clientProject: action.payload,
        loading:false
      };
    case DELETE_PROJECT:
      return {
        ...state,
        project: state.project.filter(project => project._id !== action.payload),
      };
    default:
      return state;
  }
}

export default profileReducer;
