import Experience from './Experience';
import Education from './Education';
import Spinner from '../../Spinner.js'


import isEmpty from '../../validation/isEmpty.js';


 class Profile extends Component {

    componentDidMount(){
        this.props.getCurrentProfile();
       
    }
    componentWillReceiveProps(nextProps){
          
                this.setState({
                     profile: nextProps.profile,
                });
                
     }
    render() {
        const { profile, loading} = this.props.profile; 
          let AllProfile;
        let  userExperience;
        let userEducation;
           if (profile === null || loading) {
      AllProfile = <Spinner />;
    } else {
      AllProfile =  <div className="upper">
                           <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80"  alt="user"></img>
                           <div className="userName">
                               <h2>{isEmpty(profile.username) ? "XXXXXXXX" : (profile.username)}</h2>
                               <h4>{isEmpty(profile.profession) ? "XXXXXXXX" : (profile.profession)}</h4>
                           </div>
                    </div>;
    }
     if(profile.education === null || loading){
         userEducation = <Spinner/>   
     }
     else{
           userEducation = <div>
                <Education education = {profile.education} />
           </div> 
     }

     if (profile.workexperience === null || loading) {
      userExperience = <Spinner />;
    } else {
      userExperience =  <div>
                               <Experience workexperience={profile.workexperience} />
                       </div>;
    }
    
          
        return (
            <div className="profile">
                   <NavName pageName="Profile" pageDescription="Add or Update profile information."  />
