import React, { Component } from 'react'
import "../../Css/Inbox.css";
import NavName from './NavName'
import InboxSideBar from './InboxSideBar'
import ChatArea from './InboxSideBar'
// import PropTypes from 'prop-types';
import { connect } from 'react-redux';


class Inbox extends Component {
     constructor(){
         super();
             this.state = {
                 message: '',
             };
         this.onChange = this.onChange.bind(this);
         this.onSubmit = this.onSubmit.bind(this);

     }
      onChange(e){
         this.setState({[e.target.name]: e.target.value});
     }
      onSubmit(e){
         e.preventDefault();   
         const newMessage = {
           message: this.state.email,
         };
         this.props.sendMessage(newMessage);
     }
    render() {
        const { messageRooms }   = this.props.message;
        const { messages }   = this.props.message;

        const { allUsers } = this.props.auth;
    let allRooms;
    let chatArea;


    if (messageRooms === null) {
        console.log("No rooms")
      allRooms = <div>No Rooms yet</div>;
    } 
    else {
      allRooms = <InboxSideBar allUsers={allUsers} messageRooms={messageRooms}/>;
    }
   

    if (messages === null) {
        console.log("Start a Conversation")
      chatArea = <div>No Rooms yet</div>;
    } else {
      chatArea = <ChatArea  messages={messages}/>;
    }
        return (
             <div className="inbox">
                 <NavName pageName="Inbox" pageDescription="Make a conversation with freelancer or client."  />
                <div className="inbox_body">
                    <div className="roomList">
                      <h4>All Rooms</h4>
                      {allRooms}
                    </div>
                    <div className="messageBox">
                       {chatArea}
                    </div>

                </div>
            </div>
        )
    }
}
// Inbox.propTypes = {
//       messageRooms: PropTypes.func.isRequired,
// };

const mapStateToProps = (state) => ({
     message: state.message,
     auth: state.auth,

});

export default connect(mapStateToProps, {  })(Inbox);
