import React, { Component } from 'react'
import "../../Css/Profile.css";
import {  RiGithubFill,} from "react-icons/ri";
import {  FaFacebookF, FaInstagram , FaDribbble , FaUserEdit} from "react-icons/fa";
import { AiOutlineLinkedin  } from "react-icons/ai";
import { Link} from "react-router-dom";
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { getCurrentProfile } from '../../actions/profileAction.js';
import NavName from './NavName'
import Experience from './Experience';
import Education from './Education';
import isEmpty from '../../validation/isEmpty.js';


 class Profile extends Component {

    componentDidMount(){
        this.props.getCurrentProfile();
       
    }
    componentWillReceiveProps(nextProps){
          
                this.setState({
                     profile: nextProps.profile,
                });
                
     }
    render() {
        const { profile} = this.props.profile; 
          
        let  userExperience = (
                           <div>
                               <Experience workexperience={profile.workexperience} />
                            </div>
              );
         
        let userEducation = (
                           <div>
                               <Education education={profile.education} />
                            </div>
          )
          
        return (
            <div className="profile">
                  <NavName pageName="Profile" pageDescription="Add or Update profile information."  />
                   <Link to="/www.facebooK.com" style={{textDecoration:0}}><button className="editBtn"><FaUserEdit style={{color: 'black',fontSize:20, marginRight:10}}/> Edit Profile </button>
                                    </Link>
                <div className="upperProfile">
                    <div className="PersonalInfo">
                       <div className="upper">
                           <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80"  alt="user"></img>
                           <div className="userName">
                               <h2>{isEmpty(profile.username) ? "XXXXXXXX" : (profile.username)}</h2>
                               <h4>{isEmpty(profile.profession) ? "XXXXXXXX" : (profile.profession)}</h4>
                           </div>
                       </div>
                        <div className="lower">
                            <ul className="socialLinks">
                                   <a href="https://www.youtube.com/c/jamesqquick"><li><FaFacebookF style={{color: 'lightBlue',fontSize:24}}/></li></a> 
                                    <li><FaInstagram style={{color: 'lightBlue',fontSize:24}}/></li>
                                    <li><RiGithubFill style={{color: 'lightBlue',fontSize:24}}/></li>
                                    <li><AiOutlineLinkedin style={{color: 'lightBlue',fontSize:24}}/></li>
                                    <li><FaDribbble style={{color: 'lightBlue',fontSize:24}}/></li>
                            </ul>
                             <button className="portfolioSite">PortFolio Site</button>
                        </div>
                    </div>
                    
                     <div className="aboutUser">
                        <h2>About</h2>
                        <p>
                            {isEmpty(profile.description) ? "XXXXXXXX" : (profile.description)}
                        </p>
                    </div>
                </div>
                <div className="lowerProfile">
                     <div className="otherInfo">
                       
                         <ul>          
                                <li>Experience: <span>{isEmpty(profile.experience) ? "XXXXX" : (profile.experience)}</span></li>
                                <li>Hourly Charge: <span>{isEmpty(profile.hourlyRate) ? "XXXXX" : (profile.hourlyRate)}</span></li>
                                <li>Availibilty: <span>{isEmpty(profile.availibilty) ? "XXXXX" : (profile.availibilty)}h/w</span></li>
                                <li>Address: <span>{isEmpty(profile.address) ? "XXXXX" : (profile.address)}</span></li>
                                <li>Phone: <span>{isEmpty(profile.phone) ? "XXXXX" : (profile.phone)}</span></li>
                                <li>Age: <span>{isEmpty(profile.age) ? "XXXXX" : (profile.age)}</span></li>
                        </ul>
                    </div>
                    <div className="skillsAndLanguage">
                        <div className="skills">
                             <h4>Skills</h4>
                                    <ul>
                                        {isEmpty(profile.skills) ? "No data" : (profile.skills.map(skills => (
                     <li>{skills}</li> 
                 )))}
                                    </ul>
                        </div>
                        <hr/>
                        <div className="skills">
                             <h4>Langauges</h4>
                                    <ul>
                                        {isEmpty(profile.languages) ? "No data" : (profile.languages.map(languages => (
                     <li>{languages}</li> 
                 )))}
                                    </ul>
                                    
                        </div>
                        
                    </div>
                    
                </div>

                <div className="profileCredentials">
                     <div className="education">
                        {userEducation}
                    </div>
                    <div className="experience">   
                        {userExperience}
                    </div>
                    
                </div>        
            </div>
        )
    }
}


Profile.propTypes = {
  getCurrentProfile: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  profile: state.profile,
});
export default connect(mapStateToProps, {  getCurrentProfile })(Profile);
