import axios from 'axios';
import {
  REGISTER_USER,
} from './types';



// Register User
export const register = formData => async dispatch => {
  try {
    const res = await axios.post('/users', formData);
    return {
      type: REGISTER_USER,
      payload: res.data
    }
   } catch (err) {
    console.log(err);
  }
};
