import axios from 'axios';
import {
  GET_ERRORS,
  // REGISTER_USER,
} from './types';


// Register User
export const register = (formData, history) =>  dispatch => {
   axios
   .post('http://localhost:5000/register', formData)
   .then(res=> history.push("/login"))
   .catch(err => 
    dispatch({
      type: GET_ERRORS,
      payload:err.response.data
    }))
};
