import React, { Component } from 'react';
import "../../Css/Project.css";
import { deleteProject } from '../../actions/projectAction'
import PropTypes from 'prop-types';
import { connect } from 'react-redux';


 class Project extends Component {
     constructor(){
         super();
             this.state = {
                 project_id: '',
                
              
             };
         this.onClick = this.onClick.bind(this);

     }

      onClick(e){
        this.setState({project_id: e.target.value});
        
         const id = {
           project_id: this.state.project_id,
         };
         this.props.deleteProject(id);
     }
   render() {
      const {project} = this.props.project;
        const { user} = this.props.auth;
     return(
     <div classname="project" >
  
     {project.map(projectData =>( 
             <div className="projectBody" key={projectData._id}> 
                  <h3 className="projectTitle">{projectData.projectTitle}</h3>
                  <h4 className="projectDescription">{projectData.projectDescription}</h4>
                  <ul className="projectSkills">{projectData.projectSkills.map(skills => (
                     <li>{skills}</li> 
                 ))}
                 </ul>
                  <h4 className="projectBudget">{projectData.projectBudget}</h4>  

                  {user.user.id === projectData.user ? (
                     <button  value = {projectData._id} onClick={this.onClick()} className="delete" >Delete</button>
                  ): (
                     <button className="apply" >Apply</button>
                  )}     
            </div>
         ))}
  </div>
  )
   }
}

Project.propTypes = {
  deleteProject: PropTypes.func.isRequired,
 
};

const mapStateToProps = (state) => ({
 project: state.project,
 auth: state.auth,

});

export default connect(mapStateToProps, { deleteProject })(
  Project
);
