import React, { Component } from 'react';
import "../../Css/Project.css";
import { deleteProject, applyOnProject } from '../../actions/projectAction'
import PropTypes from 'prop-types';
import { connect } from 'react-redux';


 class Project extends Component {
 

      onDeleteClick(id) {
    this.props.deleteProject(id);
  }
     onApplyClick(id) {
      this.props.applyOnProject(id);
  }
     
   render() {
       const {project, user} = this.props;
     return(
     <div classname="project" >
  
     {project.map(projectData =>( 
             <div className="projectBody" key={projectData._id}> 
                  <h3 className="projectTitle">{projectData.projectTitle}</h3>
                  <h4 className="projectDescription">{projectData.projectDescription}</h4>
                  <ul className="projectSkills">{projectData.projectSkills.map(skills => (
                     <li>{skills}</li> 
                 ))}
                 </ul>
                  <h4 className="projectBudget">{projectData.projectBudget}</h4>  

                  {user.user.id === projectData.user ? (
                     <form><button type="button" onClick={this.onDeleteClick.bind(this, projectData._id)}className="delete" >Delete</button></form>
                  ): (
                    <form><button  type="button" onClick={this.onApplyClick.bind(this, projectData._id)} className="apply" >Apply</button></form> 
                  )}     
            </div>
         ))}
  </div>
  )
   }
}

Project.propTypes = {
  deleteProject: PropTypes.func.isRequired,
  applyProject: PropTypes.func.isRequired,

 
};

const mapStateToProps = (state) => ({
});

export default connect(mapStateToProps, { deleteProject, applyOnProject })(
  Project
);
