import React, { Component } from 'react';
import "../../Css/Project.css";
import { deleteProject, applyProject, applicants } from '../../actions/projectAction'
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from "react-router-dom";

 class ProjectInD extends Component {
 
      onDeleteClick(id) {
    this.props.deleteProject(id);
  }
     onApplyClick(id) {
        const { user } = this.props.auth;
         const newApplicant = {
            email: user.user.email,
          };
      this.props.applyProject(id,newApplicant);
      
  }
  onSeeApplicantsClick() {
          this.props.applicants( this.props.history);
         }
     
   render() {
       const {project, user} = this.props;
     return(
     <div classname="project" >
  
     {project.map((projectData, i) =>
             <div className="projectBody" key={i}> 
                  <h3 className="projectTitle">{projectData.projectTitle}</h3>
                  <h4 className="projectDescription">{projectData.projectDescription}</h4>
                  <ul className="projectSkills">{projectData.projectSkills.map(skills => (
                     <li>{skills}</li> 
                 ))}
                 </ul>
                  <h4 className="projectBudget">{projectData.projectBudget}</h4>  
                 <div className="buttons">
                          {user.user.id ===! projectData.user ? (
                              <button>USer</button>
                        ): (

                            projectData.applicants ? ( projectData.applicants.map(applicant=>
                              (
                             user.user.id  === applicant.user  ? (<button className="apply" >Applied</button>):( <button  type="button" onClick={this.onApplyClick.bind(this, projectData._id)} className="apply" >Apply</button>)
                              ))): (<button  type="button" onClick={this.onApplyClick.bind(this, projectData._id)} className="apply" >Apply</button>)

                             
                             
                             
                        )}     
                  </div>
            </div>
         )}
  </div>
  )
   }
}

ProjectInD.propTypes = {
  deleteProject: PropTypes.func.isRequired,
  applyProject: PropTypes.func.isRequired,

 
};

const mapStateToProps = (state) => ({
  auth:state.auth
});

export default connect(mapStateToProps, {applyProject,deleteProject,  applicants })(withRouter(ProjectInD));
