import React, { Component } from 'react'
import { showAllProject } from '../../actions/projectAction.js'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import Project from './Project';

class AsClient extends Component {
 
    componentDidMount(){
        this.props.showAllProject();
    }
     componentWillReceiveProps(nextProps){
            if(nextProps.project){
                this.setState({
                    project: nextProps.project
                });
            }
     }

    render() {
        const { project } = this.props.project;
         
        const display = (
            <div >
                <ul>
                  {project.map( item => 
                    <li key={item.id}>{item.projectTitle}</li>
                  )}
         </ul>
      </div>
        ) 
     
        return (
            <div>
           {display}
            </div>
        )
    }
}
AsClient.propTypes = {
  showAllProject: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  project: PropTypes.object.isRequired
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  project: state.project
});
export default connect(mapStateToProps, {  showAllProject })(AsClient);
