import {
  GET_ERRORS
} from '../actions/types';


const initialState = {
  
};



function errorReducer(state = initialState, action) {
  const { type, payload } = action;

  switch (type) {
     case GET_ERRORS:
      return {
        ...state,
        payload
      }
    
    default:
      return state;
  }
}


export default errorReducer;
