import React , { useState } from 'react'
import "../../Css/Login.css"
import { Link, Redirect  } from "react-router-dom";
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { login } from '../../actions/auth';

const Login = ({ login, isAuthenticated }) => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');


    const loginBtn = e => {

        if (isAuthenticated) {
    return <Redirect to="/user" />;
  }

    }
    return (
        <div className="login">
            <Link to="/" style={{textDecoration:0}}>
                 <h1 className="ejobs">eJobs</h1>
            </Link>
            <div className="loginBox">
                <h3>Welcome Back</h3> 
                <form className="loginForm">
                    <h5>Email</h5>
                    <input type='text' value={email} onChange={e => setEmail(e.target.value)} />

                    <h5>Password</h5>
                    <input type='password' value={password} onChange={e => setPassword(e.target.value)} />
                    <div className="other">
                        <div>No Account.<Link to="/register" style={{textDecoration:0}}><span className="accountRouting">Register</span></Link></div>
                        <button type='submit' onClick={loginBtn} className='loginBtn'>Login</button>
                    </div>
                </form>

       
            </div>
               
        </div>
    )
}

Login.propTypes = {
  login: PropTypes.func.isRequired,
  isAuthenticated: PropTypes.bool
};

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated
});

export default connect(mapStateToProps, { login })(Login);
