import {
  GET_ERRORS,
  CLEAR_ERRORS
} from './types';



export  const getErrors = (err) =>{
  return{
    type: GET_ERRORS,
    payload: err.payload.data
  }
}

export const clearErrors =  () =>{
  return{
    type: CLEAR_ERRORS,
  }
}
