import React, { Component } from 'react';
import "../../Css/Applicant.css";
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from "react-router-dom";
import {   Link } from "react-router-dom";
import { getIndividualProfile } from '../../actions/profileAction'



 class Applicant extends Component {

 
         onProfileClick(id) {
            console.log(id);
                 this.props.getIndividualProfile(id);
                 this.props.history.push('/profile');
             }

  
    
     
   render() {
       const {project } = this.props;
     return(
     <div classname="applicant" >
  
     {project.map((projectData, i) =>
             <div className="applicantList" key={i}> 
                  <ul>{projectData.applicants.map(skills => (
                     <li>{skills.user}</li> 
                 ))}
                
                 </ul> 
                   <div>
                        <Link to="/applicants" style={{textDecoration:0}}>
                           <button type="button" onClick={this.onProfileClick.bind(this, projectData._id)} className="visitProfile" >Visit Profile</button>
                        </Link>
                        <Link to="/applicants" style={{textDecoration:0}}>
                           <button type="button" className="MessageUser" >Message User</button>
                        </Link>
                   </div>
                       
            </div>
         )}
  </div>
  )
   }
}

Applicant.propTypes = {
  getIndividualProfile: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,

 
};

const mapStateToProps = (state) => ({
});

export default connect(mapStateToProps, {  getIndividualProfile  })(withRouter(Applicant));
