import axios from 'axios';
import {
  REGISTER_USER,
  
} from './types';


// Register User
export const register = formData => async dispatch => {
  try {
    const res = await axios.post('http://localhost:5000/register', formData);

    dispatch({
      type: REGISTER_USER,
      payload: res.data
    });
  } catch (err) {
    console.log(err.response.data.errors) ;

  }
};
