import React, { Component }  from 'react';
import "../../Css/User.css";
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { logout } from '../../actions/authAction.js';
import { clearCurrentProfile } from '../../actions/profileAction.js';
import AddCredentials from './AddCredentials';
import { withRouter } from "react-router-dom";
import { RiDashboardLine, RiMessage2Line, RiUserLine ,RiLogoutBoxRLine} from "react-icons/ri";
import {  BsFilePost } from "react-icons/bs";
import {  AiOutlineProject } from "react-icons/ai";
import {  BrowserRouter as Router ,Switch, Route, Link,NavLink} from "react-router-dom";
import DashBoard from './Dashboard';
import Profile from './Profile';
import Inbox from './Inbox';
import AllProjects from './AllProjects';
import PostProject from './PostProject';
import EditProfile from './EditProfile';






class User extends Component {

   logoutUser(e){
         e.preventDefault();
          this.props.clearCurrentProfile();
          this.props.logout();
          this.props.history.push('/');
     }
      render() {
          
        //   const { isAuthenticated, user} = this.props.auth;
    return (
        <Router>
        <div className="user">     
            <div className="navbar">
                <div className="user">
                    <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                    <p>user.email</p>
                </div>
                <div className="navname">
                    <ul>
                        <NavLink activeStyle={{backgroundColor:"aqua", height:"auto",borderRadius:5}} to="/"  style={{textDecoration:0}}><li><span className="navItem"><RiDashboardLine style={{color: 'gray',fontSize:20, marginRight:25}}/>Dashboard</span></li></NavLink>
                        
                        <NavLink activeStyle={{backgroundColor:"aqua", height:"auto",borderRadius:5}} to="/profile" style={{textDecoration:0}}><li><span className="navItem"><RiUserLine style={{color: 'gray',fontSize:20, marginRight:25}}/>Profile</span></li></NavLink>

                        <NavLink activeStyle={{backgroundColor:"aqua", height:"auto",borderRadius:5}} to="/inbox" style={{textDecoration:0}}><li><span className="navItem"><RiMessage2Line style={{color: 'gray',fontSize:20, marginRight:25}}/>Inbox</span></li></NavLink>

                        <NavLink activeStyle={{backgroundColor:"aqua", height:"auto",borderRadius:5}} to="/allProjects" style={{textDecoration:0}}><li><span className="navItem"><AiOutlineProject style={{color: 'gray',fontSize:20, marginRight:25}}/>All Projects</span></li></NavLink>

                        <NavLink activeStyle={{backgroundColor:"aqua", height:"auto",borderRadius:5}} to="/postproject" style={{textDecoration:0}}><li><span className="navItem"><BsFilePost style={{color: 'gray',fontSize:20, marginRight:25}}/>Post Project</span></li></NavLink>

                        <li className="logoutLI" onClick={this.logoutUser.bind(this)}><span className="navItem">
                               <RiLogoutBoxRLine style={{color: 'gray',fontSize:20, marginRight:25}}/>Logout 
                        </span></li>
                    </ul>
                </div>

            </div>

 {/* for small screen html */}

            <div className="SmallNav">
                <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                <div className="nav">
                    <ul>
                         <NavLink to="/" exact activeStyle={  
             {color:'red'}  
          } to="/" style={{textDecoration:0}}><li><RiDashboardLine style={{color: 'gray',fontSize:20}}/></li></NavLink>

                        <Link to="/profile" style={{textDecoration:0}}><li><RiUserLine style={{color: 'gray',fontSize:20}}/></li></Link> 

                        <Link to="/inbox" style={{textDecoration:0}}><li><RiMessage2Line style={{color: 'gray',fontSize:20}}/></li></Link>

                        <Link to="/allprojects" style={{textDecoration:0}}><li><AiOutlineProject style={{color: 'gray',fontSize:20}}/> </li></Link> 

                        <Link to="/postproject" style={{textDecoration:0}}><li><BsFilePost style={{color: 'gray',fontSize:20}}/> </li></Link>

                        <li className="logoutLI" onClick={this.logoutUser.bind(this)}><RiLogoutBoxRLine style={{color: 'gray',fontSize:20}}/></li>
                    </ul>
                </div>
            </div>
            <div className="navbody">
               
                <Switch>
                        <Route  path="/editProfile" component={EditProfile}/>
                            
                        <Route  path="/profile" component={Profile}/>
                             
                        <Route path="/addCredentials" component={AddCredentials}/>
                             
                        <Route  path="/inbox" component={Inbox}/>
                            
                        <Route path="/allprojects" component={AllProjects}/>

                        <Route path="/postproject" component={PostProject}/>
                           
                        <Route   path="/" component={DashBoard}/>  
                       
                            
                </Switch>
            </div>
            

        </div>
    </Router>)
      }
}



User.propTypes = {
  clearCurrentProfile: PropTypes.func.isRequired,
  logout: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps,{  logout, clearCurrentProfile })(withRouter(User));
