import {
   GET_PROJECT,
  PROJECT_LOADING,
  GET_CLIENT_PROJECT
} from '../actions/types';

const initialState = {
  project: null,
  clientProject: null,
  loading: false
};



function profileReducer(state = initialState, action) {

  switch (action.type) {
     case PROJECT_LOADING:
      return {
        ...state,
        loading: true
        
      };
      case GET_PROJECT:
      return {
        ...state,
        project: action.payload,
        loading:false
      };
      
      case GET_CLIENT_PROJECT:
      return {
        ...state,
        clientProject: action.payload,
        loading:false
      };
    
    default:
      return state;
  }
}

export default profileReducer;
