import React, { Component } from 'react'
import "../../Css/Profile.css";
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { showAllProject } from '../../actions/projectAction.js'
import Spinner from '../../Spinner.js'


 class Applicants extends Component {

    componentDidMount(){
        this.props.showAllProject();
    }
    componentWillReceiveProps(nextProps){
                this.setState({
                     project: nextProps.project,
                });
                
     }
    render() {
           const { project,loading } = this.props.project;
         
    let Allprojects;

    if (project === null || loading) {
      Allprojects = <Spinner />;
    } else {
       Allprojects = <h2>{project.applicants}</h2>;
    }
          
        return (
            <div >
               {Allprojects}
            </div>
        )
    }
}


Applicants.propTypes = {
     showAllProject: PropTypes.func.isRequired,
  
};

const mapStateToProps = (state) => ({
  project: state.project,
});

export default connect(mapStateToProps, { showAllProject  })(Applicants);
