import React, { Component } from 'react'
import "../../Css/Chat.css";
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { sendMessage } from '../../actions/messageAction.js';
import { FiSend } from "react-icons/fi";

 class Chat extends Component {
      constructor(){
         super();
            this.state = {
                 text: '',
                 otherPerson: ''
             };
         this.onChange = this.onChange.bind(this);

     }
    onChange(e){
         this.setState({[e.target.name]: e.target.value});
     }
     
      onSubmit(roomname){
         const newMessage = {
           text: this.state.text,
           otherPerson: roomname
         };
        console.log(newMessage);
     }
    //   onSendMessageClick(roomname){ 
        
    //     // this.props.sendMessage(newMessage);
    //  }
    render() {
          const { roomname  } = this.props.message;
        const { messages, user } = this.props;
        console.log(roomname);

        return (
                  <div className="chat"> 
                           <div className="topInfo">
                               <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                               {/* {allUsers.map((alluser,i) => 
                              roomname ===  alluser._id  ? (<h5>{allUsers._id}</h5>): (<h5>{allUsers._id}</h5>))} */}
                              <h5>{roomname}</h5>
                           </div>
                            <div className="messageBody">
                                    <div className="messageArea">
                                        {messages.map((message , i) =>
                                  message.fromPerson ===  user.user.id ? (<div className="userMessage">
                                            <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                                            <p>{message.text}</p>
                                        </div>) : (<div className="clientMessage">
                                            <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar"/>
                                            <p>{message.text}</p>
                                        </div>)
                                        
                                        )}
                                    </div>
                                    <div className="messageForm">
                                        <form onSubmit={this.onSubmit} autoComplete="off" >
                                            <input  name ='text'
                                                value={this.state.name} 
                                                onChange={this.onChange} type="text"/>
                                            <button className="sendMsg" type='submit'><FiSend style={{color: 'gray',fontSize:20, marginRight:25}}/></button>
                                        </form>
                                    </div>
                            </div>
              
            </div>
        )
    }
}

Chat.propTypes = {
      sendMessage: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
      message: state.message
    
});

export default connect(mapStateToProps, { sendMessage  })(Chat);
