import axios from 'axios';

import {
  GET_PROFILE,
  GET_PROFILES,
  PROFILE_LOADING,
  CLEAR_CURRENT_PROFILE,
  GET_ERRORS
} from './types';



export const getCurrentProfile = () =>  dispatch => {
  dispatch(setProfileLoading());
  axios.get('http://localhost:5000/profile')
  .then(res=> 
    dispatch({
        type: GET_PROFILE,
        payload:res.data
    }))
    .catch(err => 
        dispatch({
        type: GET_PROFILE,
        payload:{}
    }));
};
export const getAllProfile = () =>  dispatch => {
  dispatch(setProfileLoading());
  axios.get('http://localhost:5000/allProfiles')
  .then(res=> 
    dispatch({
        type: GET_PROFILES,
        payload:res.data
    }))
    .catch(err => 
        dispatch({
        type: GET_ERRORS,
        payload:{}
    }));
};

export const getIndividualProfile = (id , history) =>  dispatch => {
  dispatch(setProfileLoading());
  axios.post(`http://localhost:5000/individualProfile/${id}`)
  .then(res=> history.push("/profile"))
    .catch(err => 
        dispatch({
        type: GET_ERRORS,
        payload:{}
    }));
};

export const setProfileLoading = () =>{
     return{
         type: PROFILE_LOADING
     }
}

export const clearCurrentProfile = () =>{
     return{
         type: CLEAR_CURRENT_PROFILE
     }
}

export const setProfileData = (editProfile, history) =>  dispatch => {
  axios.post('http://localhost:5000/profile', editProfile)
  .then(res=> history.push("/profile"))
   .catch(err => 
    dispatch({
    }))
};

export const addExperience = async (expData) => async dispatch => {
 await axios
    .post('http://localhost:5000/addExperience', expData)
    .then(res => alert("Data Added"))
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};
export const addEducation = (eduData) =>async dispatch => {
 await axios
    .post('http://localhost:5000/addEducation', eduData)
    .then(res => alert("Data Added"))
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};
