import React from 'react';
import "../../Css/Project.css";
import { deleteAProject } from '../../actions/projectAction'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

function Project({project,user }) {
  
  const onDeleteClick = (id) => {
         deleteAProject(id);
  };

  return (
    <div classname="project" >
  
     {project.map(projectData =>( 

        
             <div className="projectBody" key={projectData._id}> 

                  <h3 className="projectTitle">{projectData.projectTitle}</h3>
                  <h4 className="projectDescription">{projectData.projectDescription}</h4>
                  <ul className="projectSkills">{projectData.projectSkills.map(skills => (
                     <li>{skills}</li> 
                 ))}
                 </ul>
                  <h4 className="projectBudget">{projectData.projectBudget}</h4>  

                  {user.user.id === projectData.user ? (
                     <button  onClick={() => onDeleteClick(projectData._id)} className="delete" >Delete</button>
                  ): (
                     <button className="apply" >Apply</button>
                  )}     
            </div>
         ))}
  </div>
  );
}


Project.propTypes = {
  deleteAProject: PropTypes.func.isRequired,
  
};

const mapStateToProps = (state) => ({
  project: state.project,
});

export default connect(mapStateToProps,{ deleteAProject  })(Project);
