import React, { Component } from 'react'
import "../../Css/Inbox.css";
import NavName from './NavName'

export default class Inbox extends Component {
     constructor(){
         super();
             this.state = {
                 message: '',
             };
         this.onChange = this.onChange.bind(this);
         this.onSubmit = this.onSubmit.bind(this);

     }
      onChange(e){
         this.setState({[e.target.name]: e.target.value});
     }
      onSubmit(e){
         e.preventDefault();
         
        //  const newMessage = {
        //    message: this.state.email,
        //  };
     }
    render() {
        return (
             <div className="inbox">
                 <NavName pageName="Inbox" pageDescription="Make a conversation with freelancer or client."  />
                <div className="inbox_body">
                    <div className="messageList">
                       <div className="messagedUser">
                         <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar" />
                         <h4>Name</h4>
                       </div>
                       <div className="messagedUser">
                         <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar" />
                         <h4>Name</h4>
                       </div>
                    </div>
                    <div className="messageBox">
                        <div className="topInfo">
                            <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar" />
                            <h5>Name</h5>
                        </div>
                         <div className="messageBody">
                            <div className="messageArea"></div>
                                <div className="messageForm">
                                    <form autoComplete="off" className="registerForm" onSubmit={this.onSubmit}>
                                        <input  name= 'message'
                                                    value={this.state.name} 
                                                    onChange={this.onChange} type="text"/>
                                    </form>
                                </div>

                        </div>
                    </div>

                </div>
            </div>
        )
    }
}
