import axios from 'axios';
import {
  GET_PROJECT,
  PROJECT_LOADING,
  GET_CLIENT_PROJECT,
  GET_ERRORS
} from './types';



export const postProject = (newProject, history) =>  dispatch => {
  axios.post('http://localhost:5000/project', newProject)
  .then(res=> history.push("/"))
   .catch(err => 
    dispatch({
    }))
};


export const showAllProject = () =>  dispatch => {
  dispatch(setProjectLoading());
  axios.get('http://localhost:5000/project')
  .then(res=> 
    dispatch({
        type: GET_PROJECT,
        payload:res.data
    }))
    .catch(err => 
        dispatch({}));
};

export const clientProject = () =>  dispatch => {
  dispatch(setProjectLoading());
  axios.get('http://localhost:5000/clientProject')
  .then(res=> 
    dispatch({
        type: GET_CLIENT_PROJECT,
        payload:res.data
    }))
    .catch(err => 
        dispatch({
          type: GET_ERRORS,
      payload:err.response.data
        }));
};


export const setProjectLoading = () =>{
     return{
         type: PROJECT_LOADING
     }
}
