import React, { Component } from 'react';
import "../../Css/InboxSideBar.css";
import { getAllMessages } from '../../actions/messageAction'
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from "react-router-dom";

 class InboxSideBar extends Component {

      onSeeMessageClick(id) {
          this.props.getAllMessages(id);
         }
     
   render() {
       const {messageRooms , allUsers } = this.props;
     return(
            <div className="inboxSideBar"> 
             {messageRooms.map((roomList, i) =>
                      <div  onClick={this.onSeeMessageClick.bind(this, roomList.otherPerson)}       className="messagedUser"  key={i}>
                            <img src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8ZmFjZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80" alt="avatar" />
                            <h5>{allUsers.map((users, i) =>
                            users._id === roomList.otherPerson ? ( <h5>{users.email}</h5>): null
                              
            )}</h5>
                        </div>
            )}
            </div>
  )
   }
}

InboxSideBar.propTypes = {
  getAllMessages: PropTypes.func.isRequired,

};

const mapStateToProps = (state) => ({
});

export default connect(mapStateToProps, { getAllMessages })(withRouter(InboxSideBar));
