        dispatch({}));
};


export const setProjectLoading = () =>{
     return{
         type: PROJECT_LOADING
     }
}

export const deleteAProject = (project_id) => dispatch => {
  axios
    .delete(`/delete` ,{ id:project_id})
    .then(res =>
       dispatch({
        type: DELETE_PROJECT,
        payload: project_id
      })
    )
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );
};
