import React, { Component } from 'react'
import { showAllProject } from '../../actions/projectAction.js'
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class AsClient extends Component {

    componentDidMount(){
        this.props.showAllProject();
    }
     componentWillReceiveProps(nextProps){
            if(nextProps.errors){
                this.setState({
                    project: nextProps.projects
                });
            }
     }

    render() {
        const { project} = this.props;
 
  const listItems = project.map((number) =>
    <div key={number.toString()}
              value={project.projectTitle} />
  );
     
        return (
            <div>
                {listItems}
            </div>
        )
    }
}
AsClient.propTypes = {
  showAllProject: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  project: PropTypes.object.isRequired
};

const mapStateToProps = (state) => ({
  auth: state.auth,
  project: state.project
});
export default connect(mapStateToProps, {  showAllProject })(AsClient);
