import {
  GET_ERRORS,
  CLEAR_ERRORS
} from './types';



export const getErrors =  decoded =>{
  return{
    type: GET_ERRORS,
    payload: decoded
  }
}

export const clearErrors =  decoded =>{
  return{
    type: CLEAR_ERRORS,
    payload: decoded
  }
}
